-- 7. Soupe aux pois traditionnelle
INSERT INTO recipes (slug, title, excerpt, introduction, conclusion, prep_time, cook_time, total_time, servings, servings_unit, difficulty, ingredients, instructions, faq, featured_image, published_at)
VALUES (
  'soupe-aux-pois-traditionnelle',
  'Soupe aux Pois Traditionnelle',
  'L''authentique soupe aux pois québécoise: pois jaunes, lard salé et sarriette. Un classique réconfortant de notre patrimoine culinaire.',
  '<p>La <strong>soupe aux pois</strong> est un pilier de la cuisine traditionnelle québécoise. Héritée des premiers colons français, cette soupe nourrissante a réchauffé des générations de Québécois pendant les longs hivers.</p><p>Cette recette authentique utilise du lard salé (ou flanc de porc) et de la sarriette, l''herbe emblématique de notre cuisine. Le secret? Un long mijotage qui fait fondre les pois en une purée crémeuse.</p>',
  '<p>Servez cette soupe fumante avec du pain de ménage grillé et beurré. Les restes sont encore meilleurs réchauffés le lendemain!</p>',
  15, 180, 195, 8, 'portions', 'facile',
  '[{"title": "Ingrédients", "items": [{"name": "pois jaunes entiers", "quantity": "310", "unit": "g", "note": "rincés"}, {"name": "flanc de porc salé", "quantity": "115", "unit": "g", "note": "coupé en deux"}, {"name": "oignons hachés", "quantity": "375", "unit": "g"}, {"name": "beurre", "quantity": "30", "unit": "ml"}, {"name": "carottes en dés", "quantity": "145", "unit": "g"}, {"name": "céleri en dés", "quantity": "135", "unit": "g"}, {"name": "eau", "quantity": "1.5", "unit": "litre"}, {"name": "feuilles de laurier", "quantity": "3", "unit": ""}, {"name": "sarriette ciselée", "quantity": "2", "unit": "c. à thé", "note": "ou 1/2 c. à thé moulue"}]}]',
  '[{"step": 1, "title": "Tremper les pois", "content": "Faire tremper les pois 4 heures ou toute une nuit, couverts d''eau. Égoutter et jeter l''eau de trempage."}, {"step": 2, "title": "Dessaler le porc", "content": "Faire tremper le flanc de porc 15 minutes dans l''eau froide pour le dessaler légèrement. Égoutter."}, {"step": 3, "title": "Faire revenir les légumes", "content": "Dans une grande casserole, faire dorer les oignons dans le beurre. Ajouter les carottes et le céleri, cuire 5 minutes."}, {"step": 4, "title": "Mijoter", "content": "Ajouter l''eau, les pois, le porc et les feuilles de laurier. Porter à ébullition puis réduire le feu. Couvrir et laisser mijoter 2h30 à 3 heures."}, {"step": 5, "title": "Préparer le porc", "content": "Retirer le porc de la casserole. Enlever le gras, hacher la viande en petits dés et remettre dans la soupe."}, {"step": 6, "title": "Assaisonner", "content": "Ajouter la sarriette. Rectifier l''assaisonnement en sel et poivre. Servir bien chaud."}]',
  '[{"question": "Pourquoi faire tremper les pois?", "answer": "Le trempage ramollit les pois et réduit le temps de cuisson. Il aide aussi à les rendre plus digestibles."}, {"question": "Peut-on utiliser des pois cassés?", "answer": "Oui, mais ils cuiront plus vite (environ 1h30). La texture sera plus lisse."}, {"question": "Qu''est-ce que la sarriette?", "answer": "Une herbe aromatique typique du Québec, aussi appelée herbe à fèves. Elle parfume traditionnellement les légumineuses."}, {"question": "Comment épaissir la soupe?", "answer": "En fin de cuisson, écrasez une partie des pois avec le dos d''une cuillère ou utilisez un pied-mélangeur partiellement."}, {"question": "Peut-on congeler cette soupe?", "answer": "Oui! Elle se congèle très bien jusqu''à 3 mois. Décongelez au frigo et réchauffez doucement."}]',
  'https://images.unsplash.com/photo-1547592166-23ac45744acd?w=1200',
  NOW()
) RETURNING id;
