-- ============================================================
-- Import batch 2 d'épices pour La Route des Épices
-- 80 épices avec noms FR/EN
-- ============================================================

ON CONFLICT (slug) DO UPDATE SET
  name_fr = EXCLUDED.name_fr,
  name_en = EXCLUDED.name_en,
  categories = EXCLUDED.categories,
  taste_profile = EXCLUDED.taste_profile,
  is_published = EXCLUDED.is_published;

-- Afficher le nombre d'épices
SELECT COUNT(*) as total_spices FROM spices WHERE is_published = true;
